tinymce.PluginManager.add('gmap', function (editor, url) {
    // Add a button that opens a window
    editor.addButton('gmap', {
        text: 'gmap',
        icon: false,
        onclick: function () {
            // Open window
            editor.windowManager.open({
                title: 'Gmap plugin',
                //body: [
                //    { type: 'textbox', name: 'title', label: 'Title' }
                //],
                //onsubmit: function (e) {
                //    // Insert content when the window form is submitted
                //    editor.insertContent('Title: ' + e.data.title);
                //}
                url: '/assets/tinymce/plugins/gmap/pickmap.html',
                width: 400,
                height: 400
            });
        }
    });
    editor.addMenuItem('gmap', {
        text: 'gmap plugin',
        context: 'tools',
        onclick: function () {
            // Open window with a specific url
            editor.windowManager.open({
                title: 'gmap plugin',
                url: '/assets/tinymce/plugins/gmap/pickmap.html',
                width: 400,
                height: 400,
                buttons: [{
                    text: 'Close',
                    onclick: 'close'
                }]
            });
        }
    });

    return {
        getMetadata: function () {
            return {
                name: "Example plugin",
                url: "http://exampleplugindocsurl.com"
            };
        }
    };
});